import React, { useState, useEffect, useCallback, useContext } from 'react';
import io from 'socket.io-client';
import useSound from 'use-sound';
import config from '../../../config';
import LatestMessagesContext from '../../../contexts/LatestMessages/LatestMessages';
import TypingMessage from './TypingMessage';
import Header from './Header';
import Footer from './Footer';
import Message from './Message';
import '../styles/_messages.scss';
import initial_botty_msg from '../../../common/constants/initialBottyMessage';

const enduser = 'enduser';
const chatbot = 'chatbot';

const socket = io(
  config.BOT_SERVER_ENDPOINT,
  { transports: ['websocket', 'polling', 'flashsocket'] }
);

const initial_msg = {
  message: initial_botty_msg,
  id: Date.now(),
  user: chatbot
};

function scrollToBottom(){
  const msgList = document.querySelector("#message-list");
  window.scrollTo(0, msgList.innerHeight);
}

function Messages() {
  const [playSend] = useSound(config.SEND_AUDIO_URL);
  const [playReceive] = useSound(config.RECEIVE_AUDIO_URL);
  const { setLatestMessage } = useContext(LatestMessagesContext);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([initial_msg]);
  const [botTyping, setBotTyping] = useState(false);

  useEffect(() => {
    // socket.off('bot-message');
    socket.on('bot-message', (message) => {
      setBotTyping(false);

      setMessages([...messages, { message, user: chatbot, id: Date.now() }]);

      setLatestMessage(chatbot, message);

      //playReceive();

      scrollToBottom();
    });

  }, [messages]);

  useEffect(()=>{
    scrollToBottom();
  }, []);

  const onChangeMessage = (event) => {
    setMessage(event.target.value)
  };

  const sendMessage = useCallback(() => {
    if (!message) {
       return false; 
      }

    setMessages([...messages, { message, user: enduser, id: Date.now() }]);

    // playSend();

    scrollToBottom();

    socket.emit('user-message', message);

    setMessage('');

    document.querySelector("#user-message-input").value = '';
  }, [messages, message]);

  return (
    <div className="messages">
      <Header />
      <div className="messages__list" id="message-list">
        {messages.map((msg, index) => (
            <Message nextMessage={messages[index + 1]} message={msg} botTyping={botTyping} />
          ))}
          {botTyping ? <TypingMessage /> : null}
      </div>
      <Footer message={message} sendMessage={sendMessage} onChangeMessage={onChangeMessage} />
    </div>
  );
}

export default Messages;
