import React, { useContext } from 'react';
import io from 'socket.io-client';
import useSound from 'use-sound';
import config from '../../../config';
import LatestMessagesContext from '../../../contexts/LatestMessages/LatestMessages';
import TypingMessage from './TypingMessage';
import Header from './Header';
import Footer from './Footer';
import Message from './Message';
import '../styles/_messages.scss';
import initial_botty_msg from '../../../common/constants/initialBottyMessage';

const enduser = 'enduser';
const chatbot = 'chatbot';

const socket = io(
  config.BOT_SERVER_ENDPOINT,
  { transports: ['websocket', 'polling', 'flashsocket'] }
);

const initial_msg = {
  message: initial_botty_msg,
  id: Date.now(),
  user: chatbot
};

function msgToBottom(){
  const msgList = document.querySelector("#message-list");
  window.scrollTo(0, msgList.innerHeight);
}

function Messages() {
  const [playSend] = useSound(config.SEND_AUDIO_URL);
  const [playReceive] = useSound(config.RECEIVE_AUDIO_URL);
  const { setLatestMessage } = useContext(LatestMessagesContext);
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([initial_msg]);

  useEffect(()=>{
    msgToBottom();
  }, []);

  return (
    <div className="messages">
      <Header />
      <div className="messages__list" id="message-list">
        {messages.map((msg, index) => (
            <Message nextMessage={messages[index + 1]} message={msg} botTyping={botTyping} />
          ))}
          {botTyping ? <TypingMessage /> : null}
      </div>
      <Footer message={message} sendMessage={sendMessage} onChangeMessage={onChangeMessage} />
    </div>
  );
}

export default Messages;
